import React, { useEffect, useState } from 'react'
import axios from 'axios'
import { useParams, useNavigate } from 'react-router-dom'
import AuthorForm from '../components/AuthorForm'
const UpdateAuthor = ({ authors, setAuthors }) => {
  const { id } = useParams()
  const [author, setAuthor] = useState({})
  const [loaded, setLoaded] = useState(false)
  let navigate = useNavigate()
  useEffect(() => {
    axios.get('http://localhost:8000/authors/' + id).then((res) => {
      setAuthor(res.data)
      setLoaded(true)
    })
  }, [])

  const handleUpdate = (author) => {
    axios
      .put('http://localhost:8000/authors/' + id + '/edit', author)
      .then((res) => {
        setAuthors([...authors, author])
      })
      .then((res) => navigate('/'))
      .catch((err) => console.error(err))
  }

  return (
    <div>
      <h2>Edit Author</h2>
      {loaded && (
        <AuthorForm onSubmitHandler={handleUpdate} initialName={author.name} />
      )}
    </div>
  )
}

export default UpdateAuthor
