import React, { useEffect, useState } from 'react'
import axios from 'axios'
import { useParams, useNavigate } from 'react-router-dom'
import AuthorForm from '../components/AuthorForm'
const AddAuthor = ({ authors, setAuthors }) => {
  const { id } = useParams()
  const [author, setAuthor] = useState([])
  const [loaded, setLoaded] = useState(false)
  let navigate = useNavigate()
  useEffect(() => {
    axios
      .get('http://localhost:8000/authors/' + id)
      .then((res) => {
        console.log(res.data)
        setAuthor(res.data.Authors)
        setLoaded(true)
      })
      .catch((err) => console.log(err))
  }, [])

  const handleCreate = (author) => {
    axios
      .post('http://localhost:8000/authors/new', {
        author,
      })
      .then((res) => setAuthors([...authors, res.data]))
      .then((res) => navigate('/'))
      .catch((err) => console.error(err))
  }

  return (
    <div>
      <h2>new Author</h2>
      {loaded && <AuthorForm author={author} onSubmitProp={handleCreate} />}
    </div>
  )
}

export default AddAuthor
