import React, { useEffect, useState } from 'react'
import axios from 'axios'
import { Link } from 'react-router-dom'
const Authors = (props) => {
  const { removeFromDom, authors, setAuthors } = props
  const [loaded, setLoaded] = useState(false)

  useEffect(() => {
    axios
      .get('http://localhost:8000/authors')

      .then((res) => {
        setAuthors(res.data.Authors)

        setLoaded(true)
        console.log(res.data.Authors)
      })
      .catch((err) => console.log(err))
  }, [authors])
  const handleDelete = (id) => {
    axios
      .delete(`http://localhost:8000/authors/${id}/delete`)
      .then((res) => {
        removeFromDom(id)
      })
      .catch((err) => console.log(err))
  }
  return (
    <table>
      <thead>
        <tr>
          <td>Name</td>
          <td>Actions</td>
        </tr>
      </thead>
      <tbody>
        {authors.map((author, i) => (
          <tr key={i}>
            <td>
              <Link to={'/authors/' + author._id}>{author.name}</Link>
            </td>
            <td>
              <Link to={'/authors/' + author._id + '/edit'}>edit</Link>

              <button
                onClick={(e) => {
                  handleDelete(author._id)
                }}
              >
                delete
              </button>
            </td>
          </tr>
        ))}
      </tbody>
    </table>
  )
}

export default Authors
